sp_widget.client_script:
function($rootScope, $scope) {
  var c = this;

  c.refreshProfessors = function() {
    c.server.update().then(function(response) {
      c.data.professors = response.data.professors;
    });
  };

  // Function to handle selection of a professor
  c.selectItem = function(idx) {
    var id = c.data.professors[idx].sys_id;
    $rootScope.professorID = id; // Set the global professorID
    c.data.professorID = id; // Set local ID for visibility control
    $rootScope.$emit('selectProfessor', id);
  };

  // Listen for subject selection from Widget 3
  $rootScope.$on('subjectSelected', function(event, subject) {
    c.data.selectedSubject = subject; // Set the selected subject filter
  });
}

