sp_widget.script:
(function() {
  data.professors = [];
  var profGR = new GlideRecord('x_snc_createnotes_professors');

  // Optionally check if input.subject is defined and not null
  if (input && input.subject) {
    profGR.addQuery('subject', input.subject);
  }

  profGR.orderByDesc('sys_created_on');
  profGR.setLimit(options.maximum_records_to_display);
  profGR.query();

  while (profGR.next()) {
    var professorObj = {};
    $sp.getRecordDisplayValues(professorObj, profGR, 'name, office, subject, sys_id');
    data.professors.push(professorObj);
  }
})();
